// src/App.js - EbookVoice AI Landing Page - IMPROVED METRICS UI
import React, { useState, useEffect, useRef } from 'react';
import './App.css';

// Add custom styles for improved metrics layout and player
const customStyles = `
  .summary-stats-improved {
    background: rgba(255, 255, 255, 0.05);
    border-radius: 16px;
    padding: 24px;
    margin: 20px 0;
    border: 1px solid rgba(255, 255, 255, 0.1);
  }
  
  .stat-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 12px 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.08);
  }
  
  .stat-row:last-child {
    border-bottom: none;
  }
  
  .stat-label {
    font-size: 14px;
    color: #cccccc;
    font-weight: 500;
    text-align: left;
  }
  
  .stat-value {
    font-size: 16px;
    color: #ffffff;
    font-weight: 700;
    text-align: right;
  }
  
  .stat-value.voice-name {
    color: #667eea;
    font-size: 14px;
    font-weight: 600;
    max-width: 150px;
    text-align: right;
    word-break: break-word;
  }
  
  /* Improved Audio Player Styles */
  .player-header {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 16px;
    padding: 20px;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  }
  
  .book-info {
    text-align: center;
    width: 100%;
  }
  
  .player-center-controls {
    display: flex;
    justify-content: center;
    width: 100%;
  }
  
  .chapters-list-btn {
    background: rgba(102, 126, 234, 0.15);
    border: 1px solid rgba(102, 126, 234, 0.3);
    border-radius: 12px;
    padding: 10px 16px;
    color: #ffffff;
    font-size: 14px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 8px;
  }
  
  .chapters-list-btn:hover {
    background: rgba(102, 126, 234, 0.25);
    border-color: rgba(102, 126, 234, 0.5);
    transform: translateY(-2px);
  }
  
  .chapters-icon {
    font-size: 16px;
  }
  
  .chapters-text {
    font-weight: 600;
  }
  
  .chapters-count {
    color: #cccccc;
    font-size: 13px;
  }
  
  /* Fixed Audio Player Progress Styles */
  .progress-section {
    padding: 16px 20px;
    display: flex;
    flex-direction: column;
    gap: 12px;
  }
  
  .time-info {
    display: flex;
    justify-content: space-between;
    font-size: 14px;
    color: #cccccc;
    font-weight: 500;
  }
  
  .seek-bar {
    position: relative;
    width: 100%;
    height: 6px;
    cursor: pointer;
    padding: 8px 0;
  }
  
  .seek-track {
    position: relative;
    width: 100%;
    height: 6px;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 3px;
    overflow: visible;
  }
  
  .seek-progress {
    height: 100%;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    border-radius: 3px;
    transition: width 0.1s ease;
  }
  
  .seek-handle {
    position: absolute;
    top: 50%;
    width: 16px;
    height: 16px;
    background: #ffffff;
    border: 2px solid #667eea;
    border-radius: 50%;
    transform: translateY(-50%);
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
    transition: all 0.2s ease;
    cursor: pointer;
  }
  
  .seek-handle:hover {
    transform: translateY(-50%) scale(1.2);
    box-shadow: 0 4px 12px rgba(102, 126, 234, 0.4);
  }
  
  /* Clean Upgrade Prompt Styles */
  .upgrade-prompt.enhanced {
    background: rgba(255, 255, 255, 0.05);
    border-radius: 20px;
    padding: 30px;
    margin: 30px 0;
    border: 1px solid rgba(255, 255, 255, 0.1);
    text-align: center;
  }
  
  .upgrade-prompt.enhanced h4 {
    margin: 0 0 24px 0;
    color: #ffffff;
    font-size: 24px;
    font-weight: 700;
  }
  
  .upgrade-comparison {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 20px;
    margin: 24px 0;
    flex-wrap: wrap;
  }
  
  .current-tier-info, .next-tier-info {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 8px;
  }
  
  .tier-badge {
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 14px;
    font-weight: 600;
  }
  
  .tier-badge.sample {
    background: rgba(255, 255, 255, 0.1);
    color: #cccccc;
  }
  
  .tier-badge.free {
    background: rgba(102, 126, 234, 0.2);
    color: #667eea;
  }
  
  .tier-details {
    font-size: 13px;
    color: #cccccc;
    text-align: center;
  }
  
  .arrow {
    font-size: 20px;
    color: #667eea;
    font-weight: bold;
  }
  
  .upgrade-benefits {
    display: flex;
    flex-direction: column;
    gap: 12px;
    margin: 24px 0;
    text-align: left;
    max-width: 300px;
    margin-left: auto;
    margin-right: auto;
  }
  
  .benefit-item {
    color: #ffffff;
    font-size: 15px;
    font-weight: 500;
  }
  
  .upgrade-actions {
    display: flex;
    flex-direction: column;
    gap: 12px;
    margin-top: 30px;
  }
  
  .upgrade-btn-primary {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border: none;
    padding: 16px 24px;
    border-radius: 12px;
    font-weight: 600;
    font-size: 16px;
    cursor: pointer;
    transition: all 0.3s ease;
  }
  
  .upgrade-btn-primary:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 35px rgba(102, 126, 234, 0.4);
  }
  
  .try-another-btn {
    background: transparent;
    color: #cccccc;
    border: 1px solid #444;
    padding: 12px 20px;
    border-radius: 12px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
  }
  
  .try-another-btn:hover {
    border-color: #667eea;
    color: #ffffff;
  }
  
  /* Clean Error Styles */
  .conversion-error {
    background: rgba(255, 255, 255, 0.05);
    border-radius: 20px;
    padding: 40px 30px;
    margin: 30px 0;
    border: 1px solid rgba(255, 107, 107, 0.2);
    text-align: center;
  }
  
  .error-animation {
    margin-bottom: 30px;
  }
  
  .error-icon {
    font-size: 64px;
    margin-bottom: 20px;
    display: block;
  }
  
  .conversion-error h3 {
    color: #ff6b6b;
    font-size: 24px;
    font-weight: 700;
    margin: 0 0 16px 0;
  }
  
  .error-message {
    background: rgba(255, 107, 107, 0.1);
    border: 1px solid rgba(255, 107, 107, 0.2);
    border-radius: 12px;
    padding: 20px;
    margin: 20px 0;
    color: #ffffff;
    font-size: 16px;
    line-height: 1.5;
    text-align: left;
  }
  
  .error-actions {
    display: flex;
    flex-direction: column;
    gap: 12px;
    margin-top: 30px;
  }
  
  .retry-btn {
    background: linear-gradient(135deg, #ff6b6b 0%, #ff8787 100%);
    color: white;
    border: none;
    padding: 16px 24px;
    border-radius: 12px;
    font-weight: 600;
    font-size: 16px;
    cursor: pointer;
    transition: all 0.3s ease;
  }
  
  .retry-btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 35px rgba(255, 107, 107, 0.4);
  }
  
  .reset-btn {
    background: transparent;
    color: #cccccc;
    border: 1px solid #444;
    padding: 12px 20px;
    border-radius: 12px;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
  }
  
  .reset-btn:hover {
    border-color: #ff6b6b;
    color: #ffffff;
  }
  
  @media (max-width: 768px) {
    .summary-stats-improved {
      padding: 20px 16px;
    }
    
    .stat-label {
      font-size: 13px;
    }
    
    .stat-value {
      font-size: 15px;
    }
    
    .stat-value.voice-name {
      font-size: 13px;
      max-width: 120px;
    }
    
    .chapters-list-btn {
      padding: 8px 14px;
      font-size: 13px;
    }
    
    .chapters-icon {
      font-size: 14px;
    }
    
